import React from 'react';
import {Component} from 'react';
import ReactDOM from 'react-dom';
import './App.css';
import NavBar from './navbar.js';
import ReactRevealText from 'react-reveal-text';
import {IconContext} from "react-icons"
import {GrMailOption} from 'react-icons/gr';



      // <div className="Photo">
      //   <img src="profile.png" style={{"height": "25%", "width": "25%"}} alt="Photo of me"/>
      // </div>

function App() {

  const [darkMode, setDarkMode] = React.useState(getInitialMode);
  React.useEffect(() => {localStorage.setItem("dark", JSON.stringify(darkMode))}, [darkMode])


  function getInitialMode() {
    const savedMode = JSON.parse(localStorage.getItem("dark"));
    return savedMode || false
  }

  return (
    <div className={darkMode ? "dark-mode" : "light-mode"}>
      <NavBar/>

      <div className="home" id="home">
        <ReactRevealText
          text="NABEEL NAIYER"
          show
      />
      </div>
      <div className="aboutMe" id="aboutme">


          <IconContext.Provider value={{ style: {fontSize: '30px'}}}>
            <div>
              <button className="modeToggle" onClick={() => setDarkMode(prevMode => !prevMode)}><GrMailOption/></button>
            </div>
          </IconContext.Provider>



      </div>
    </div>
  );
}

export default App;
